changeSet.sql:
-- -------------------- -- migration of axis -- -- --------------------
insert into QUERY_AGGREGATION_COLUMN (query_model_id, label, aggregation_operation, aggregation_rank, cuf_id, query_column_id) select cac.query_id, cac.label, cac.axis_operation, cac.axis_rank, cac.cuf_id, cac.chart_column_id from CHART_AXIS_COLUMN cac inner join QUERY_MODEL qm on cac.query_id = qm.query_model_id where qm.strategy != 'MAIN';
insert into QUERY_ORDERING_COLUMN (query_model_id, query_column_id, order_rank, order_dir, cuf_id, order_operation) select qac.query_model_id, qac.query_column_id, qac.aggregation_rank, 'ASC', qac.cuf_id, qac.aggregation_operation from QUERY_AGGREGATION_COLUMN qac;
-- ------------------------ -- migration of measures -- -- ------------------------
insert into QUERY_PROJECTION_COLUMN (query_column_id, query_model_id, label, projection_operation, cuf_id, projection_rank) select cmc.chart_column_id, cmc.query_id, cmc.label, cmc.measure_operation, cmc.cuf_id, cmc.measure_rank from CHART_MEASURE_COLUMN cmc inner join QUERY_MODEL qm on cmc.query_id = qm.query_model_id where qm.strategy != 'MAIN';
insert into QUERY_ORDERING_COLUMN(query_model_id, query_column_id, order_dir, cuf_id, order_operation, order_rank) select cmc.query_id, cmc.chart_column_id, 'ASC', cmc.cuf_id, cmc.measure_operation, cmc.measure_rank + ( select count(*) from CHART_AXIS_COLUMN cac where cac.query_id = cmc.query_id ) as adjust_rank from CHART_MEASURE_COLUMN cmc inner join QUERY_MODEL qm on cmc.query_id = qm.query_model_id where qm.strategy != 'MAIN';
-- ----------------------- -- migration of filters -- -- -----------------------
insert into QUERY_FILTER_COLUMN (query_column_id, query_model_id, filter_operation, cuf_id) select cfc.chart_column_id, cfc.query_id, cfc.filter_operation, cfc.cuf_id from CHART_FILTER cfc inner join QUERY_MODEL qm on cfc.query_id = qm.query_model_id where qm.strategy != 'MAIN';
insert into QUERY_FILTER_VALUES (filter_value, query_filter_id) select cfv.filter_value, qfc.query_filter_id from CHART_FILTER_VALUES cfv inner join CHART_FILTER cfc on cfv.filter_id = cfc.filter_id inner join QUERY_MODEL qm on cfc.query_id = qm.query_model_id inner join QUERY_FILTER_COLUMN qfc on ( cfc.chart_column_id = qfc.query_column_id and cfc.query_id = qfc.query_model_id and cfc.filter_operation = qfc.filter_operation ) where qm.strategy != 'MAIN';

update CHART_AXIS_COLUMN cac set cac.chart_definition_id = ( select cd.chart_id from QUERY_MODEL qm inner join CHART_DEFINITION cd on qm.query_model_id = cd.query_id where qm.strategy = 'MAIN' and cac.query_id = qm.query_model_id );
update CHART_FILTER cf set cf.chart_definition_id = ( select cd.chart_id from QUERY_MODEL qm inner join CHART_DEFINITION cd on qm.query_model_id = cd.query_id where qm.strategy = 'MAIN' and cf.query_id = qm.query_model_id );
update CHART_MEASURE_COLUMN cmc set cmc.chart_definition_id = ( select cd.chart_id from QUERY_MODEL qm inner join CHART_DEFINITION cd on qm.query_model_id = cd.query_id where qm.strategy = 'MAIN' and cmc.query_id = qm.query_model_id );

update CHART_AXIS_COLUMN set chart_definition_id = cd.chart_id from QUERY_MODEL qm inner join CHART_DEFINITION cd on qm.query_model_id = cd.query_id where qm.strategy = 'MAIN' and CHART_AXIS_COLUMN.query_id = qm.query_model_id;
update CHART_FILTER set chart_definition_id = cd.chart_id from QUERY_MODEL qm inner join CHART_DEFINITION cd on qm.query_model_id = cd.query_id where qm.strategy = 'MAIN' and CHART_FILTER.query_id = qm.query_model_id;
update CHART_MEASURE_COLUMN set chart_definition_id = cd.chart_id from QUERY_MODEL qm inner join CHART_DEFINITION cd on qm.query_model_id = cd.query_id where qm.strategy = 'MAIN' and CHART_MEASURE_COLUMN.query_id = qm.query_model_id;

delete from CHART_FILTER_VALUES
where filter_id in (
select filter_id from CHART_FILTER where chart_definition_id is null
);

delete from CHART_FILTER where chart_definition_id is null;

delete from CHART_AXIS_COLUMN where chart_definition_id is null;

delete from CHART_MEASURE_COLUMN where chart_definition_id is null;
