changeSet.sql:
CREATE EXTENSION IF NOT EXISTS "uuid-ossp";
UPDATE ITERATION SET UUID = uuid_generate_v4() WHERE UUID is NULL;
ALTER TABLE ITERATION ALTER COLUMN UUID SET NOT NULL;

